import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import web3 from './web3'
import lottery from './lottery';

class App extends Component {
state = {
  manager :'',
  players:[],
  balance:'',
  value:'',
  message:''
};
async ComponentDidMount() {



  const manager = await lottery.methods.manager().call();
  const players = await lottery.methods.getPlayers().call();
  const balance = await web3.eth.getbalance(lottery.options.address);
  this.setState({manager,players,balance});
}

onSumbit = async(event) => {
  event.preventDefault();
console.log("in onSubmit");
  this.setState({message:'waiting on transection success...'});
  const accounts = await web3.eth.getAccounts();
  console.log(web3.eth.getBalance("balance is :"+accounts[0]));
  await lottery.methods.enter.send({from:accounts[0],
  value: web3.utlis.toWei(this.state.value,'ether')});

  this.setState({message:'successfully sent'});
};

  render() {

    web3.eth.getAccounts().then(console.log);
    console.log('aa')
    console.log(this.state.manager);
    console.log("current provide is:" + web3.currentProvider.constructor.name);


    return (

      <div>
      <h2> Lottery Contract </h2>
      <p> This contarct is managed by {this.state.manager}
        There are currently {this.state.players.length} people comepting to
        win {web3.utils.fromWei(this.state.balance,'ether')} ether!
        </p>

        <hr />
        <form onSubmit = {this.onSubmit}>
<h4> want to try your luck? </h4>
<div>
<label> Amount of ether to enter </label>
<input
value = {this.state.value}
onChange ={event => this.setState({value:event.target.value})}
/>
</div>
<button>Enter </button>
        </form>
        <hr />
        <h1> {this.state.message} </h1>





      </div>




    );
  }
}

export default App;
