import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import web3 from './web3'
import lottery from './lottery';

class App extends Component {
state = {
  manager :'',
  players:[],
  balance:'',
  value:'',
  message:'',
  yourBalance:''
};
 async componentDidMount() {


console.log('in componentditMount');
 const manager  = await lottery.methods.manager().call();
 const players  = await lottery.methods.getPlayers().call();
 const balance  = await web3.eth.getBalance(lottery.options.address);
 const accounts = await web3.eth.getAccounts();
 console.log('manager inside componentditMount:' + manager);

  const mybalance = await web3.eth.getBalance(accounts[0]);
  console.log('balance is' + mybalance);
  this.setState({manager,players,balance});
}

onSubmit = async (event) => {

  event.preventDefault();
console.log("in onSubmit");
  this.setState({message:'waiting on transection success...'});
  const accounts = await web3.eth.getAccounts();
  //console.log('balance is:' +web3.eth.getBalance(accounts[0]));
  await lottery.methods.enter().send({from:accounts[0],
  value: web3.utils.toWei(this.state.value,'ether')});

  this.setState({message:'successfully sent'});
};



onClick = async (event) => {

const accounts = await web3.eth.getAccounts();
this.setState({message:'waiting on transection success...'});
await lottery.methods.pickWinner().send({from:accounts[0]});
this.setState({message:'winner picked!'});
};





  render() {

    //web3.eth.getAccounts().then(console.log);

    console.log('aa')
    // console.log('manager is:'+this.state.manager);
    // console.log("current provide is:" + web3.currentProvider.constructor.name);


    return (

      <div>
      <h2> Block Chain Enabled Network Configration</h2>
              <hr />




<form onSubmit = {this.onSubmit}>
<div>
<label> Data to save </label>
<input
value = {this.state.value}
onChange ={event => this.setState({value:event.target.value})}
/>
</div>
<button>Enter </button>
        </form>

        <p> This contarct is managed by {this.state.manager} There are currently {this.state.players.length} people comepting to
          win {web3.utils.fromWei(this.state.balance,'ether')} ether!
          </p>

          <hr />
          <h4> pick a winner?</h4>
          <button onClick = {this.onClick} > Pick a winner! </button>
          <hr />
          <h1> {this.state.message} </h1>



      </div>
    );
}
}

export default App;
